package com.ait.wk2;

public class IncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5, y = 10;
		System.out.println("The value of x is "+x);
		System.out.println("The value of ++x is "+ ++x);
		System.out.println("The value of x++ is "+ x++);
		System.out.println("The value of x is "+x);
		System.out.println("The value of y is "+y);
		System.out.println("The value of --y is "+ --y);
		System.out.println("The value of y-- is "+ y--);
		System.out.println("The value of y is "+ y);
	}
}
